/**
 * Method: setProxy
 *      Note: For use on Comcast devices only.
 *      Set a proxy URL of the FreeWheel HTML5 SDK, so that ad requests, FreeWheel MRM tracking URLs, third party tracking URLs and VAST tag URLs will be sent through the specified proxy server.
 * 
 *      proxyUrl - must be a valid URL with the prefix of http:// or https://.
 *      By default the proxy URL would be null, so that redirections would not be applied to ad requests, etc.
 */
_fwsdk.setProxy = function(proxyUrl){
	_fwsdk.debug("setProxy: " + proxyUrl);
	if (!_fwsdk.Util.isValidProxyUrl(proxyUrl)) return;
	_fwsdk.proxyUrl = proxyUrl.charAt(proxyUrl.length - 1) !== '/' ? proxyUrl + '/' : proxyUrl;
};

/**
 * Method: setCreativesProxy
 *      Note: For use on Comcast devices only.
 *      Set a proxy URL for creatives of the FreeWheel HTML5 SDK, so that all creative asset URLs will be sent through the specified proxy server.
 *
 *      creativesProxyUrl - must be a valid URL with the prefix of http:// or https://.
 *      By default creativesProxyUrl would be null.
 */
_fwsdk.setCreativesProxy = function(creativesProxyUrl){
	_fwsdk.debug("setCreativesProxy: " + creativesProxyUrl);
	if (!_fwsdk.Util.isValidProxyUrl(creativesProxyUrl)) return;
	_fwsdk.creativesProxyUrl = creativesProxyUrl.charAt(creativesProxyUrl.length - 1) !== '/' ? creativesProxyUrl + '/' : creativesProxyUrl;
};

/**
 * Method: setProxyAuthenticationParameter
 *      Set the query string authentication parameter of the FreeWheel HTML5 SDK so that the proxy server would authorize the redirect request.
 *
 *      authKey - the key of the authentication parameter.
 *      authValue - the value of the authentication parameter.
 */
_fwsdk.setProxyAuthenticationParameter = function(authKey, authValue){
	if (arguments.length >= 2 && authKey) {
		_fwsdk.proxyAuthenticationParameter = {
			key: authKey,
			value: authValue || ''
		};
	} else {
		_fwsdk.proxyAuthenticationParameter = null;
	}
};

_fwsdk.checkUserAgent = function(uagent){ 	
	_fwsdk.PLATFORM_IS_WINDOWSPHONE = uagent.search('windows phone') > -1;
	_fwsdk.PLATFORM_IS_IPAD = uagent.search('ipad') > -1;
	_fwsdk.PLATFORM_IS_IPHONE_IPOD = uagent.search('iphone') > -1 || uagent.search('ipod') > -1;
	_fwsdk.PLATFORM_IS_CHROMECAST = uagent.search('crkey') > -1 ;

	var getPlatformVersion = function(versionExpression) {
		var matches = uagent.match(versionExpression);
		if (matches && matches.length > 0) {
			var versionDigits = matches[0].match(/\d+/g);
			var mainVer = versionDigits[0];
			var subVer = versionDigits.length > 1 ? versionDigits[1] : 0;
			return mainVer * 1 + subVer * 0.1;
		} else {
			return 0;
		}
	};

	// TODO this method only retrieves the major version and ignores point versions
	var getBrowserVersion = function(uagent) {
		var fullVersion = '';
		var verOffset = -1;

		if ((verOffset = uagent.indexOf('opr/')) !== -1) {
			// In Opera 15+, the true version is after "OPR/" 
			fullVersion = uagent.substring(verOffset + 4);
		} else if ((verOffset = uagent.indexOf('opera')) !== -1) {
			// In older Opera, the true version is after "Opera" or after "Version"
			fullVersion = uagent.substring(verOffset + 6);
			if ((verOffset = uagent.indexOf('version')) !== -1) {
				fullVersion = uagent.substring(verOffset + 8);
			}
		} else if ((verOffset = uagent.indexOf('rv:')) !== -1) {
			// In IE 11, the true version is afer "rv:"
			fullVersion = uagent.substring(verOffset + 3);
		} else if ((verOffset = uagent.indexOf('msie')) !== -1) {
			// In earlier versions of IE, the true version is after "MSIE" in userAgent
			fullVersion = uagent.substring(verOffset + 5);
		} else if ((verOffset = uagent.indexOf('edge')) !== -1) {
			// In Edge, the true version is after "Edge" in userAgent
			fullVersion = uagent.substring(verOffset + 5);
		} else if ((verOffset = uagent.indexOf('chrome')) !== -1) {
			// In Chrome, the true version is after "Chrome" 
			fullVersion = uagent.substring(verOffset + 7);
		} else if ((verOffset = uagent.indexOf('safari')) !== -1) {
			// In Safari, the true version is after "Safari" or after "Version" 
			fullVersion = uagent.substring(verOffset + 7);
			if ((verOffset = uagent.indexOf('version')) !== -1) {
				fullVersion = uagent.substring(verOffset + 8);
			}
		} else if ((verOffset = uagent.indexOf('firefox')) !== -1) {
			// In Firefox, the true version is after "Firefox" 
			fullVersion = uagent.substring(verOffset + 8);
		} else if ((uagent.lastIndexOf(' ') + 1) < (verOffset=uagent.lastIndexOf('/'))) {
			// In most other browsers, "name/version" is at the end of userAgent 
			fullVersion = uagent.substring(verOffset + 1);
		}
		
		// trim the fullVersion string at semicolon/space if present
		var ix;
		if ((ix = fullVersion.indexOf(';')) !== -1) {
			fullVersion = fullVersion.substring(0, ix);
		}
		if ((ix=fullVersion.indexOf(' '))!==-1) {
			fullVersion = fullVersion.substring(0, ix);
		}

		var majorVersion = parseInt('' + fullVersion, 10);
		return majorVersion? majorVersion : 0;
	};

	_fwsdk.PLATFORM_IE_MOBILE_VERSION = getPlatformVersion(/iemobile\/\d+\.\d+/); 
	_fwsdk.PLATFORM_IOS_VERSION = getPlatformVersion(/os \d+_\d+/); // Match strings like "OS 10_12"
	_fwsdk.PLATFORM_ANDROID_VERSION = (function() {
		if(uagent.indexOf('transformer')> -1){
			//Workaround for Asus EEEPad browser UA be set to DESKTOP
			return 3.2;
		}else {
			var platformVersion = getPlatformVersion(/android \d+\.\d+/);
			return platformVersion !== 0 ? platformVersion : getPlatformVersion(/android \d+/);
		}
	}());

	//Including iOS UIWebView App
	_fwsdk.PLATFORM_IS_SAFARI = (_fwsdk.PLATFORM_IOS_VERSION>0 && uagent.search('applewebkit') > -1 ) || (_fwsdk.PLATFORM_ANDROID_VERSION === 0 && uagent.search('chrome') < 0 && uagent.search('safari') > -1);
	_fwsdk.PLATFORM_IS_CHROME = uagent.search('edge') < 0 && uagent.search('chrome') > -1;
	_fwsdk.PLATFORM_IS_FIREFOX= uagent.search('firefox') > -1;
	_fwsdk.PLATFORM_IS_MOBILE = (_fwsdk.PLATFORM_IOS_VERSION > 0) || (_fwsdk.PLATFORM_ANDROID_VERSION > 0);
	_fwsdk.PLATFORM_BROWSER_VERSION = getBrowserVersion(uagent);
	_fwsdk.PLATFORM_ID = _fwsdk.PLATFORM_IS_MOBILE ? ( (_fwsdk.PLATFORM_ANDROID_VERSION > 0)  ? 'Android'+_fwsdk.PLATFORM_ANDROID_VERSION : (_fwsdk.PLATFORM_IS_IPAD ? 'iPad'+_fwsdk.PLATFORM_IOS_VERSION : (_fwsdk.PLATFORM_IS_IPHONE_IPOD ? 'iPhone'+_fwsdk.PLATFORM_IOS_VERSION : 'UnknownMobile'))) : 'Desktop';
	_fwsdk.log('Device:', _fwsdk.PLATFORM_ID, 'PLATFORM_IS_SAFARI:', _fwsdk.PLATFORM_IS_SAFARI, 'PLATFORM_IS_CHROME:', _fwsdk.PLATFORM_IS_CHROME);
};

