			var videoLeft = contentVideo.style.left;
			var videoTop = contentVideo.style.top;
			var videoWidth = contentVideo.offsetWidth ? contentVideo.offsetWidth : contentVideo.style.pixelWidth;
			var videoHeight = contentVideo.offsetHeight ? contentVideo.offsetHeight : contentVideo.style.pixelHeight;
			var videoPosition = contentVideo.style.position;
			this._autoSetDisplaySizeCount++;
