_fwsdk.Capabilities.prototype = {
	init: function() {
		var defaultOnCapabilities = [
			_fwsdk.CAPABILITY_SLOT_TEMPLATE,
			_fwsdk.CAPABILITY_MULTIPLE_CREATIVE_RENDITIONS,
			_fwsdk.CAPABILITY_FALLBACK_UNKNOWN_ASSET,
			_fwsdk.CAPABILITY_FALLBACK_UNKNOWN_SITE_SECTION,
			_fwsdk.CAPABILITY_FALLBACK_ADS,
			_fwsdk.CAPABILITY_SLOT_CALLBACK,
			_fwsdk.CAPABILITY_NULL_CREATIVE,
			_fwsdk.CAPABILITY_AUTO_EVENT_TRACKING,
			_fwsdk.CAPABILITY_RENDERER_MANIFEST
		];
		for(var i = 0; i < defaultOnCapabilities.length; i++) {
			this._capabilities[defaultOnCapabilities[i]] = _fwsdk.CAPABILITY_STATUS_ON;
		}
	},
	setCapability: function(capability, status) {
		this._capabilities[capability] = status;
	},
	getCapability: function(capability) {
		return this._capabilities[capability];
	},
	parseCapabilities: function(str) {
		var ret = str;
		var capabilitiesStr = '';
		for(var i in this._capabilities) {
			if (!this._capabilities.hasOwnProperty(i)) {
				continue;
			}
			switch(this._capabilities[i]) {
			case _fwsdk.CAPABILITY_STATUS_ON :
				capabilitiesStr += '+' + i;
				break;
			case _fwsdk.CAPABILITY_STATUS_OFF :
				capabilitiesStr += '-' + i;
				break;
			}
			ret = ret.replace(new RegExp('(%2B|-|\\+)' + i, 'g'), '');
		}
		capabilitiesStr = encodeURIComponent(capabilitiesStr);
		if (ret.indexOf('flag=') > -1) {
			ret = ret.replace(/flag=/, 'flag=' + capabilitiesStr);
		} else {
			ret += '&flag=' + capabilitiesStr;
		}
		return ret;
	}
};
_fwsdk.VideoAsset = function(context){
	this._defaultTimeouts = [5, 10, 15, 30, 60, 120, 180, 300];

	this._context = context;
	this._internalState = _fwsdk.MediaInitState.instance;
	this._state = ''; // Set by Context.setVideoState; Used by AdRequest.
	this._eventCallback = '';
	this._callbackTimer = null; // Countdown timer used for triggering callback
	this._videoPlayPending = false;
	this._requestedVideoViewUrl = false;
	this._dummyInstanceId = '';
	this._delay = 0;

	// properties
	this._id = '';
	this._customId = '';
	this._duration = '';
	this._networkId = '';
	this._location = '';
	this._autoPlayType = null;
	this._fallbackId = '';
	this._viewRandom = 0;
	this._durationType = null;
	this._currentTpos = 0;
};

_fwsdk.VideoAsset.prototype = {
	setVideoAsset: function(id, duration, networkId, location, autoPlayType, videoViewRandom, idType, fallbackId, durationType) {
		if (!id) {
			_fwsdk.warn('AdRequest.setVideoAsset','id is required.');
			return;
		}
		var idHasChanged = false;
		switch(idType) {
		case _fwsdk.ID_TYPE_FW:
			idHasChanged = this._id !== id;
			this._id = id;
			break;
		case _fwsdk.ID_TYPE_GROUP:
			idHasChanged = this._id !== 'g' + id;
			this._id = 'g' + id;
			break;
		default:
			idHasChanged = this._customId !== id;
			this._customId = id;
			break;
		}
		if (idHasChanged) {
			this._eventCallback = '';
		}
		if (typeof duration === 'number') {
			this._duration = Math.round(duration * 10) / 10;
		}
		if (networkId*1 > 0) {
			this._networkId = networkId*1;
		}
		if (typeof location === 'string') {
			this._location = location;
		}
		if (typeof autoPlayType === 'number') {
			this._autoPlayType = autoPlayType;
		}
		if (videoViewRandom*1 > 0) {
			this._viewRandom = videoViewRandom*1;
		}
		if (typeof fallbackId === 'number' && fallbackId > 0) {
			this._fallbackId = '' + fallbackId;
		} else if (typeof fallbackId === 'string') {
			this._fallbackId = fallbackId.trim();
		}
		if (durationType === _fwsdk.VIDEO_ASSET_DURATION_TYPE_EXACT || durationType === _fwsdk.VIDEO_ASSET_DURATION_TYPE_VARIABLE) {
			this._durationType = durationType;
		}
		if (this._videoPlayPending){
			this.play();
		}
	},

	setVideoAssetCurrentTimePosition: function(timePosition) {
		if (!timePosition || timePosition < 0) {
			_fwsdk.warn('AdRequest.setVideoAssetCurrentTimePosition', 'valid timePosition is required');
			return;
		}
		this._currentTpos = timePosition;
	},

	setVideoState: function(videoState){
		if (videoState === _fwsdk.VIDEO_STATE_PLAYING){
			// only if current state is paused, dispatch content video resumed.
			if(this._state === _fwsdk.VIDEO_STATE_PAUSED){
				this._context.dispatchEvent(_fwsdk.EVENT_CONTENT_VIDEO_RESUMED);
			}
			this.play();
		} else if (videoState === _fwsdk.VIDEO_STATE_PAUSED || videoState === _fwsdk.VIDEO_STATE_STOPPED){
			this.pause();
			if(videoState === _fwsdk.VIDEO_STATE_PAUSED && this._state === _fwsdk.VIDEO_STATE_PLAYING){
				this._context.dispatchEvent(_fwsdk.EVENT_CONTENT_VIDEO_PAUSED);
			}
		} else if (videoState === _fwsdk.VIDEO_STATE_COMPLETED){
			this.complete();
		}
		this._state = videoState;
	},

	callback: function(skipScheduleNextTimer) {
		var skipScheduleNext = skipScheduleNextTimer;
		if (this._eventCallback) {
			this._eventCallback.process();
		} else {
			skipScheduleNext = true;
		}
		if (!skipScheduleNext){
			var nextTimeout = this._defaultTimeouts.shift();
			if (!nextTimeout) {
				nextTimeout = 300;
			}
			this._callbackTimer = new _fwsdk.Timer(nextTimeout, _fwsdk.Util.bind(this, this.callback));
			this._callbackTimer.start();
		}
	},

	getPlayheadTime: function() {
		var ret = this._delay;
		this._delay = 0;
		ret += this._callbackTimer ? this._callbackTimer.getCTValue() : 0;
		return Math.floor(ret);
	},

	play: function() {
		if (!this._context._adManager._serverURL ||
			!this._context._adManager._networkId ||
			!(this._id || this._customId)) {
			_fwsdk.warn('Server URL or Network ID or Video Asset id/customId is not set, delay video asset state change until these are provided.');
			this._videoPlayPending = true;
			if (!this._callbackTimer){
				this._callbackTimer = new _fwsdk.Timer();
				this._callbackTimer.tick();
			}
			return;
		}
		if (!this._eventCallback){
			if (this._requestedVideoViewUrl) {
				return;
			}
			this._requestedVideoViewUrl = true;
			this.requestForVideoViewCallback();
			if (!this._callbackTimer){
				this._callbackTimer = new _fwsdk.Timer();
				this._callbackTimer.tick();
			}
		} else{
			this._videoPlayPending = false;
			this._internalState.play(this);
		}
	},

	pause: function() {
		this._internalState.pause(this);
	},

	complete: function() {
		this._internalState.complete(this);
	},

	onStartPlaying: function() {
		this.callback();
	},

	onStartReplaying: function() {
		this.callback();
	},

	onPausePlaying: function() {
		this.callback(true);
		this._callbackTimer.pause();
	},

	onResumePlaying: function() {
		this._callbackTimer.start();
	},

	onCompletePlaying: function() {
		this.callback(true);
		this._callbackTimer.stop();
		this._callbackTimer = null;
		this._eventCallback = '';
		this._requestedVideoViewUrl = false;
	},

	onCompleteReplaying: function() {
		this.onCompletePlaying();
	},

	setMediaState: function(value) {
		this._internalState = value;
	},

	requestPauseBySlot: function(slot) {
		_fwsdk.debug('requestPauseBySlot');
		if (this._internalState === _fwsdk.MediaPlayingState.instance || this._internalState === _fwsdk.MediaReplayingState.instance) {
			this._context.dispatchEvent(_fwsdk.EVENT_CONTENT_VIDEO_PAUSE_REQUEST, {
				slot: slot
			});
		} else {
			_fwsdk.debug('Not sending content pause request because content video state is',  JSON.stringify(this._internalState));
		}
	},

	requestResumeBySlot: function(slot) {
		_fwsdk.debug('requestResumeBySlot');
		if (this._internalState === _fwsdk.MediaPausedState.instance || this._internalState === _fwsdk.MediaReplayPausedState.instance) {
			this._context.dispatchEvent(_fwsdk.EVENT_CONTENT_VIDEO_RESUME_REQUEST, {
				slot: slot
			});
		} else {
			_fwsdk.debug('Not sending content resume request because content video state is ',  JSON.stringify(this._internalState));
		}		
	},

	requestForVideoViewCallback: function(){
		/*
		Create a dummy context and add it to the instanceQueue. This context is use for callback purpose only.

		When ad response is received, this._context has already been removed from the instanceQueue, so it's not
		possible to use tv.freewheel.SDK._instanceQueue['Context_" + this._context._instanceId + "'].requestComplete
		anymore. So we create a dummy context and use this._dummyInstanceId to record its _instanceId.
		*/
		var dummyContext = new _fwsdk.Context(this._context._adManager);
		dummyContext._videoAsset = this;
		this._dummyInstanceId = dummyContext._instanceId;

		var requestUrl = this._context._adRequest.generateVideoViewRequestUrlWithDummyContextInstanceId(dummyContext._instanceId);

		var owner = this;
		//For Desktop and iOS3.2 above(not including 3.2) Safari, if not disabled, use hidden form to workaround cookie limitation
		if (_fwsdk.PLATFORM_SEND_REQUEST_BY_FORM) {
			_fwsdk.debug('Context.submitRequest:', 'use json2');
			_fwsdk.Util.pingURLWithForm(requestUrl, this._dummyInstanceId, true);
			owner._onMessagePosted = function(event) {
				if (typeof event.data === 'object' && event.data.hasOwnProperty('cbfn') && event.data.cbfn.indexOf('[\'Context_' + this._dummyInstanceId + '\']') > -1) {
					owner.requestComplete(event.data.response);
				}
			};
			window.addEventListener('message', owner._onMessagePosted, false);
		} else {
			_fwsdk.Util.pingURLWithScript(requestUrl);
		}
	},

	requestComplete: function(response) {
		this._delay = this._callbackTimer.tick();
		this._callbackTimer = null;
		if (_fwsdk.PLATFORM_SEND_REQUEST_BY_FORM) {
			window.removeEventListener('message', this._onMessagePosted, false);
			var ifr = document.getElementById('_fw_request_iframe_' + this._dummyInstanceId);
			document.body.removeChild(ifr);
		}
		if (response != null) {
			_fwsdk._instanceQueue['Context_' + this._dummyInstanceId] = null;
			// Parse video view url only.
			this.parse(_fwsdk.Util.getObject('siteSection.videoPlayer.videoAsset', response) || {});
			this.play();
		} else {
			_fwsdk.warn('Failed to get video view callback url.');
		}
	},

	parse: function(value) {
		if (!value) {
			return;
		}
		this._customId = value.customId;
		this._networkId = parseInt(value.networkId);

		var iter;
		for (var i = 0, arr = value.eventCallbacks || []; i < arr.length; i++) {
			iter = arr[i];
			var eventCallback = _fwsdk.EventCallback.newEventCallback(this._context, iter.name, iter.type);
			if (eventCallback && iter.name === _fwsdk.EVENT_VIDEO_VIEW) {
				eventCallback.parse(iter);
				this._eventCallback = eventCallback;
				_fwsdk.debug('Parsed video view url: ' + this._eventCallback._url);
				break;
			}
		}
	}
};
